import Vue from 'vue'
import Vuex from 'vuex'
import createPersistedState from 'vuex-persistedstate'
Vue.use(Vuex)
export default new Vuex.Store({
	plugins:[createPersistedState()],
	//持久性 reducer,部分持久性
	//公共状态
	state:{
		isShow:true
	},
	//支持异步
	actions:{
	},
	//统一管理，支持同步
	mutations:{
		changeisShow(data){
			state.isShow=!state.isShow
		}
	},
	getters: {
	  }
})
